(function($) {
  "use strict"; // Start of use strict
  
  
  	//preloader
	$(document).on('readystatechange',function(){
		//alert(document.readyState);
		var state=document.readyState;
		if(state == 'interactive')
		{
		     //alert('inter');
			 $('.contents').hide();
		}
		else if(state == 'complete')
		{
		  setTimeout(function(){    //remove this line
			  $('#preloader-1').hide();
			  $('#preloader-2').hide();
			  $('.contents').show();
			  },1000);//remove this line
		}
	});
	
	//preloader end
  
  
  
  
     
	
  
  
  
  
  
  
  // Configure tooltips for collapsed side navigation
  $('.navbar-sidenav [data-toggle="tooltip"]').tooltip({
    template: '<div class="tooltip navbar-sidenav-tooltip" role="tooltip" style="pointer-events: none;"><div class="arrow"></div><div class="tooltip-inner"></div></div>'
  })
  // Toggle the side navigation
  $("#sidenavToggler").click(function(e) {
    e.preventDefault();
    $("body").toggleClass("sidenav-toggled");
    $(".navbar-sidenav .nav-link-collapse").addClass("collapsed");
    $(".navbar-sidenav .sidenav-second-level, .navbar-sidenav .sidenav-third-level").removeClass("show");
  });
  // Force the toggled class to be removed when a collapsible nav link is clicked
    $(".navbar-sidenav .nav-link-collapse").click(function(e) {
        e.preventDefault();
        $("body").removeClass("sidenav-toggled");
	    $('#collapseExamplePages,#collapseComponents,#collapseMulti').css("display",'');    	
    });

  // /mouse leave function for compression width > 992px       
  $(".navbar-sidenav").mouseleave(function(e) {
    if($(window).width() >= 992)
	{
		e.preventDefault();
		$("body").addClass("sidenav-toggled");                      //to check =hasClass('class')
		$(".navbar-sidenav .nav-link-collapse").addClass("collapsed");
		$(".navbar-sidenav .sidenav-second-level, .navbar-sidenav .sidenav-third-level").removeClass("show");
		//$('#collapseExamplePages,#collapseComponents,#collapseMulti').addClass("showing");
		$('#collapseExamplePages,#collapseComponents,#collapseMulti').css("display",'none');
    }
	
  });
  
  $(".nav-link:not(.nav-link:first)").click(function(e){
	 e.preventDefault(); 
	 
	if($(window).width() < 992 && $(window).width() >= 768 )  
	{    
		if( $(".nav-link:visible").length == 1 ) 
		{ 
			$(".nav-link").each(function(){
				$(this).show();
			});
			$('#main-search').show();
		}
		else
		{
			$(".nav-link").each(function(){
				$(this).hide();
			});
			$('#main-search').hide();
			$(this).show();
		}	
	}	
  });
  
  
 
  // Prevent the content wrapper from scrolling when the fixed side navigation hovered over
  $('body.fixed-nav .navbar-sidenav, body.fixed-nav .sidenav-toggler, body.fixed-nav .navbar-collapse').on('mousewheel DOMMouseScroll', function(e) {
    var e0 = e.originalEvent,
      delta = e0.wheelDelta || -e0.detail;
    this.scrollTop += (delta < 0 ? 1 : -1) * 30;
    e.preventDefault();
  });
  // Scroll to top button appear
  $(document).scroll(function() {
    var scrollDistance = $(this).scrollTop();
    if (scrollDistance > 100) {
      $('.scroll-to-top').fadeIn();
    } else {
      $('.scroll-to-top').fadeOut();
    }
  });
  // Configure tooltips globally
  $('[data-toggle="tooltip"]').tooltip()
  // Smooth scrolling using jQuery easing
  $(document).on('click', 'a.scroll-to-top', function(event) {
    var $anchor = $(this);
    $('html, body').stop().animate({
      scrollTop: ($($anchor.attr('href')).offset().top)
    }, 1000, 'easeInOutExpo');
    event.preventDefault();
  });
    // /hover function for enlarging side-navbar
  /*$(".navbar-sidenav ").hover(function(e) {
    e.preventDefault();
    $("body").removeClass("sidenav-toggled");
  });*/
})(jQuery); // End of use strict
